parameters.jsonBody:
={
  "requests": [
    {
      "insertText": {
        "location": { "index": 1 },
        "text": {{ JSON.stringify($('OCR Text').item.json.string) }}
      }
    },
    {
      "updateParagraphStyle": {
        "range": {
          "startIndex": 1,
          "endIndex": {{ parseInt($('OCR Text').item.json.string.length, 10) + 1 }}
        },
        "paragraphStyle": 
          { 
            "direction": "RIGHT_TO_LEFT",
            "lineSpacing": 90,
            "spaceAbove": {"magnitude": 0, "unit": "PT"}, 
            "spaceBelow": {"magnitude": 6, "unit": "PT"}
          },
        "fields": "direction, lineSpacing, spaceAbove, spaceBelow"
      }
    },
    {
      "updateTextStyle": {
        "range": {
          "startIndex": 1,
          "endIndex": {{ $node["OCR Text"].json.string.length + 1 }}
        },
        "textStyle": {
          "fontSize": {
            "magnitude": 10,
            "unit": "PT"
          }
        },
        "fields": "fontSize"
      }
    }
  ]
}

